const defaultOptions = {
  hidden: false,
  info: true
};
export default (options => {
  options = { ...defaultOptions,
    ...options
  };
  const wrapper = document.createElement('div');
  wrapper.style = `position:fixed;top:0;left:0;right:0;bottom:0;`;

  if (options.wrapperClassName) {
    wrapper.className = options.wrapperClassName;
  }

  if (options.hidden) {
    wrapper.style.display = 'none';
  }

  const canvas = document.createElement('canvas');
  canvas.style = `width:100%;height:100%;`;
  const info = document.createElement('div');
  info.style = `position:absolute;top:1em;left:1em;color:white`;

  if (!options.info) {
    info.style.display = 'none';
  }

  wrapper.appendChild(canvas);
  wrapper.appendChild(info);
  document.body.appendChild(wrapper);
  return {
    getCanvas: () => canvas,
    getInfo: () => info,
    show: () => wrapper.style.display = 'block',
    hide: () => wrapper.style.display = 'none',
    cleanup: () => document.body.removeChild(wrapper)
  };
});
