import {
  createGameLoop,
  createStage,
  createViewport,
  PolygonBatch,
  ShapeRenderer,
  Texture,
  Vector2,
  ViewportInputHandler,
  BitmapFont,
  TextureRegion,
  Color,
} from "gdxts";

export const init = async () => {
  const stage = createStage();
  const canvas = stage.getCanvas();
  const viewport = createViewport(canvas, 1500, 2000);
  const gl = viewport.getContext();
  const shapeRenderer = new ShapeRenderer(gl);
  const batch = new PolygonBatch(gl);
  const camera = viewport.getCamera();
  const inputHandler = new ViewportInputHandler(viewport);

  const background = await Texture.load(gl, "./TetrisBg.png");
  const bgRight = await Texture.load(gl, "./bg1.png");
  const block = await Texture.load(gl, "./7251.png");
  const redBlock = await Texture.load(gl, "./RedBlock.png");
  const purpleBlock = await Texture.load(gl, "./PurpleBlock.png");
  const orangeBlock = await Texture.load(gl, "./OrangeBlock.png");
  const yellowBlock = await Texture.load(gl, "./YellowBlock.png");
  const blueBlock = await Texture.load(gl, "./BlueBlock.png");
  const blueSkyBlock = await Texture.load(gl, "./BlueSkyBlock.png");
  const greenBlock = await Texture.load(gl, "./GreenBlock.png");

  //tetromino
  const squareBlock = [
    [1, 1],
    [1, 1],
  ];

  //I
  const iBlock1 = [2, 2, 2, 2];
  const iBlock2 = [[2], [2], [2], [2]];

  //J
  const jBlock1 = [
    [3, 0, 0],
    [3, 3, 3],
  ];
  const jBlock2 = [
    [3, 3],
    [3, 0],
    [3, 0],
  ];
  const jBlock3 = [
    [3, 3, 3],
    [0, 0, 3],
  ];
  //L
  const LBlock1 = [
    [0, 0, 4],
    [4, 4, 4],
  ];
  const LBlock2 = [
    [4, 4],
    [0, 4],
    [0, 4],
  ];
  //s
  const sBlock1 = [
    [0, 5, 5],
    [5, 5, 0],
  ];
  const sBlock2 = [
    [5, 0],
    [5, 5],
    [0, 5],
  ];
  //t
  const tBlock1 = [
    [0, 6, 0],
    [6, 6, 6],
  ];
  const tBlock2 = [
    [6, 0],
    [6, 6],
    [6, 0],
  ];
  const tBlock3 = [
    [7, 7, 7],
    [0, 7, 0],
  ];
  const tBlock4 = [
    [0, 7],
    [7, 7],
    [0, 7],
  ];
  //z
  const zBlock1 = [
    [8, 8, 0],
    [0, 8, 8],
  ];
  const zBlock2 = [
    [8, 0],
    [8, 8],
    [0, 8],
  ];

  //config game
  let blockPosition = new Vector2(417, 1833);
  let currentBlock = squareBlock;
  let timeDelay = 0;

  function getRandomInt(min: number, max: number) {
    return Math.floor(Math.random() * (max - min)) + min;
  }

  function drawBlock() {
    for (let i = 0; i < currentBlock.length; i++) {
      for (let j = 0; j < currentBlock[0].length; j++) {
        batch.draw(yellowBlock, blockPosition.x, blockPosition.y, 83, 83);
      }
    }
  }

  createGameLoop((delta: number) => {
    timeDelay += delta;
    console.log(timeDelay);
    gl.clear(gl.COLOR_BUFFER_BIT);
    shapeRenderer.setProjection(camera.projectionView.values);
    shapeRenderer.begin();
    shapeRenderer.rect(true, 0, 0, 1000, 2000);
    shapeRenderer.end();
    if (timeDelay >= 1.5) {
      blockPosition.y -= 84;
      timeDelay = 0;
    }

    window.addEventListener("keydown", function (e) {
      if (e) {
        drawBlock();
      }
    });

    //draw background
    batch.setProjection(camera.projectionView.values);
    batch.begin();
    batch.draw(background, 0, 0, 1000, 2000);
    drawBlock();
    batch.draw(bgRight, 1000, 0, 500, 2000);
    batch.end();
  });
};

init();
