import {
  createGameLoop,
  createStage,
  createViewport,
  InputEvent,
  PolygonBatch,
  ShapeRenderer,
  Texture,
  Vector2,
  ViewportInputHandler,
} from "gdxts";
import { init2 } from "./PokemonGame";
export const init = async () => {
  const stage = createStage();
  const canvas = stage.getCanvas();
  const viewport = createViewport(canvas, 600, 1000);
  const gl = viewport.getContext();
  const background = await Texture.load(gl, "./flappy-bird.gif");
  const obstacles = await Texture.load(gl, "./obstacle.webp");
  const gameovericon = await Texture.load(gl, "./gameover.png");

  const shapeRenderer = new ShapeRenderer(gl);
  const batch = new PolygonBatch(gl);
  const camera = viewport.getCamera();
  const inputHandler = new ViewportInputHandler(viewport);

  const characterTexture = await Texture.load(gl, "./bird.png");
  let characterPosition = new Vector2(250, 500);
  let rotationOfBird = 0;
  let obstaclePosition = new Vector2(595, 195);
  let obstaclePosition2 = new Vector2(795, 195);

  gl.clearColor(0, 0, 0, 1);
  let lastClick = false;
  let speedGame = 200;
  let obstacleSpeed = speedGame;
  let count = 0;
  let heightObstacleUp = 300;
  let heightObstacleDown = 270;
  let firstClick = false;

  inputHandler.addEventListener(InputEvent.TouchStart, (x, y) => {
    count = 0;
    rotationOfBird = -0.1;
    if (firstClick) {
      speedGame = 200;
    }
    firstClick = true;
    lastClick = true;
  });

  function moveOfBird(delta: number) {
    if (lastClick) {
      lastClick = true;
      characterPosition.y = characterPosition.y + (speedGame *= 1.02) * delta;
      count += speedGame * delta;
      if (rotationOfBird <= 0.25) {
        setTimeout(() => {
          rotationOfBird += 0.05;
          // console.log(rotationOfBird);
        }, 50);
      }

      if (count >= 80) {
        lastClick = false;
        count--;
      }
    }

    if (lastClick === false) {
      characterPosition.y -= speedGame * delta;
      if (count >= -30 && count < 80) {
        count -= (speedGame *= 1.025) * delta;
      }
      if (rotationOfBird >= -1) {
        rotationOfBird -= 0.07;
      }
    }
  }

  function getRandomInt(min: number, max: number) {
    return Math.floor(Math.random() * (max - min)) + min;
  }

  function obstacleMove(delta: number) {
    obstaclePosition.x -= obstacleSpeed * delta;
    obstaclePosition2.x -= obstacleSpeed * delta;

    if (obstaclePosition.x < -200) {
      obstaclePosition.set(695, 195);
      obstaclePosition2.set(695, 195);
      let optionOfObstacle = getRandomInt(1, 5);
      console.log(optionOfObstacle);
      switch (optionOfObstacle) {
        case 1:
          heightObstacleDown = 150;
          heightObstacleUp = 500;
          break;

        case 2:
          heightObstacleDown = 250;
          heightObstacleUp = 400;
          break;
        case 3:
          heightObstacleDown = 350;
          heightObstacleUp = 300;
          break;
        case 4:
          heightObstacleDown = 450;
          heightObstacleUp = 200;
          break;
        case 5:
          heightObstacleDown = 550;
          heightObstacleUp = 125;
          break;
        default:
          break;
      }
      // heightObstacleUp = getRandomInt(200, 800);
      // heightObstacleDown = getRandomInt(200, 800);

      if (obstacleSpeed <= 500) {
        obstacleSpeed *= 1.13;
      }
    }
  }

  createGameLoop((delta: number) => {
    gl.clear(gl.COLOR_BUFFER_BIT);
    shapeRenderer.setProjection(camera.projectionView.values);
    shapeRenderer.begin();
    shapeRenderer.rect(true, 0, 0, 600, 1000);
    shapeRenderer.end();
    // console.log(characterPosition.y, speedGame);

    if (firstClick === true) {
      moveOfBird(delta);
      obstacleMove(delta);
    }

    batch.setProjection(camera.projectionView.values);
    batch.begin();
    batch.draw(background, 0, 0, 600, 1000);
    batch.draw(
      characterTexture,
      characterPosition.x,
      characterPosition.y,
      60,
      60,
      30,
      30,
      rotationOfBird
    );

    batch.draw(
      obstacles,
      obstaclePosition.x,
      obstaclePosition.y,
      150,
      heightObstacleDown // height ben duoi default = 270 // max 500 min 150
    );
    batch.draw(
      obstacles,
      obstaclePosition.x,
      obstaclePosition.y + 610,
      150,
      heightObstacleUp, // height ben tren // default = 300 // max 500 min 150
      75,
      100,
      47.13
    );

    // obstacles 2
    batch.draw(
      obstacles,
      obstaclePosition2.x,
      obstaclePosition2.y,
      150,
      heightObstacleDown // height ben duoi default = 270 // max 500 min 150
    );
    batch.draw(
      obstacles,
      obstaclePosition2.x,
      obstaclePosition2.y + 610,
      150,
      heightObstacleUp, // height ben tren // default = 300 // max 500 min 150
      75,
      100,
      47.13
    );

    if (characterPosition.y <= 190) {
      firstClick = false;
      lastClick = false;
      speedGame = 0;
      batch.draw(gameovericon, 50, 500, 500, 250);
    }
    batch.end();
  });
};

init();
