    //   frame = 8;
    //   while (frame <= 12) {
    //     frame++;
    //     region = walkAnimation.getKeyFrame(frame, PlayMode.NORMAL);
    //   }
    //   changePosition();
    // }

    // console.log(frame + " " + i);

    batch.setProjection(camera.projectionView.values);
    batch.begin();
    changePosition(delta);

    // batch.draw(
    //   characterTexture,
    //   10,
    //   10,
    //   500,
