import {
  createGameLoop,
  createStage,
  createViewport,
  InputEvent,
  PolygonBatch,
  ShapeRenderer,
  Texture,
  Vector2,
  ViewportInputHandler,
  BitmapFont,
  TextureRegion,
  Animation,
  PlayMode,
} from "gdxts";

export const init = async () => {
  const stage = createStage();
  const canvas = stage.getCanvas();
  const viewport = createViewport(canvas, 1000, 2000);
  const gl = viewport.getContext();

  const background = await Texture.load(gl, "./TetrisBg.png");
};
