import {
  createStage,
  createViewport,
  createGameLoop,
  ShapeRenderer,
  Texture,
  TextureRegion,
  PolygonBatch,
  Animation,
  PlayMode,
  ViewportInputHandler,
  InputEvent,
  Vector2,
  Color,
} from "gdxts";

import Walkable from "walkable";

const init = async () => {
  let walkable = new Walkable(500, 1000);

  const stage = createStage();
  const canvas = stage.getCanvas();
  const viewport = createViewport(canvas, 500, 1000);
  const gl = viewport.getContext();
  const background = await Texture.load(gl, "./pokemon-bg.jpg");

  const mapData = await fetch("./test.tmj").then((res) => res.json());
  const obstacles = mapData.layers
    .find((l: any) => l.name === "obstacles")
    .objects.map((rect: any) => ({
      ...rect,
      y: 1000 - rect.y - rect.height,
    }));

  for (let rect of obstacles) {
    walkable.addRect(rect.width, rect.height, rect.x, rect.y);
  }

  // eslint-disable-next-line
  // const texture = await Texture.load(gl, "./charizard.png");
  const characterTexture = await Texture.load(gl, "./spritesheet.png");
  // const apple = await Texture.load(gl, "./apple.png");
  const regions = TextureRegion.splitTexture(characterTexture, 4, 4);

  const walkDownAnimation = new Animation(regions.slice(0, 4), 0.2);
  const walkLeftAnimation = new Animation(regions.slice(4, 8), 0.2);
  const walkRightAnimation = new Animation(regions.slice(8, 12), 0.2);
  const walkUpAnimation = new Animation(regions.slice(12, 16), 0.2);

  const shapeRenderer = new ShapeRenderer(gl);
  const batch = new PolygonBatch(gl);
  const camera = viewport.getCamera();
  const inputHandler = new ViewportInputHandler(viewport);
  const characterPosition = new Vector2(250, 500);
  // var path = walkable.findPath(
  //   characterPosition.x,
  //   characterPosition.y,
  //   250,
  //   500,
  //   1
  // );

  let rotation = 0;
  let frame = 0;
  gl.clearColor(0, 0, 0, 1);

  const path: number[] = [];
  let nextPositon = new Vector2(250, 500);
  inputHandler.addEventListener(InputEvent.TouchStart, (x, y) => {
    const nextPositon = inputHandler.getTouchedWorldCoord();
    walkable.findPath(
      characterPosition.x,
      characterPosition.y,
      nextPositon.x,
      nextPositon.y,
      10,
      path
    );
    rotation =
      (Math.atan2(
        nextPositon.y - characterPosition.y,
        nextPositon.x - characterPosition.x
      ) *
        180) /
      Math.PI;
    // console.log(applePosition);
    // path = walkable.findPath(
    //   characterPosition.x,
    //   characterPosition.y,
    //   nextPositon.x,
    //   nextPositon.y,
    //   1
    // );
    // console.log(path);
  });

  // let applePosition = {
  //   x: getRandomInt(50, 400),

  //   y: getRandomInt(30, 900),
  // };

  function getRandomInt(min: number, max: number): number {
    return Math.floor(Math.random() * (max - min)) + min;
  }

  const moveSpeed = 150;

  createGameLoop((delta: number) => {
    gl.clear(gl.COLOR_BUFFER_BIT);
    shapeRenderer.setProjection(camera.projectionView.values);
    shapeRenderer.begin();
    shapeRenderer.rect(true, 0, 0, 500, 1000);
    shapeRenderer.end();
    let region = walkDownAnimation.getKeyFrame(frame, PlayMode.NORMAL);
    if (
      characterPosition.x !== nextPositon.x ||
      characterPosition.y !== nextPositon.y
    ) {
      frame += delta;
      if (rotation >= 0 && rotation < 90) {
        if (characterPosition.y < nextPositon.y) {
          region = walkUpAnimation.getKeyFrame(frame, PlayMode.LOOP);
          characterPosition.y = Math.min(
            characterPosition.y + delta * moveSpeed,
            nextPositon.y
          ); // increase y
        }
        if (characterPosition.x < nextPositon.x) {
          region = walkRightAnimation.getKeyFrame(frame, PlayMode.LOOP);
          characterPosition.x = Math.min(
            characterPosition.x + delta * moveSpeed,
            nextPositon.x
          ); // increase x
        }
      }
      if (rotation >= -90 && rotation < 0) {
        if (characterPosition.x < nextPositon.x) {
          region = walkRightAnimation.getKeyFrame(frame, PlayMode.LOOP);
          characterPosition.x = Math.min(
            characterPosition.x + delta * moveSpeed,
            nextPositon.x
          ); // increase x
        }
        if (characterPosition.y > nextPositon.y) {
          region = walkDownAnimation.getKeyFrame(frame, PlayMode.LOOP);
          characterPosition.y = Math.max(
            characterPosition.y - delta * moveSpeed,
            nextPositon.y
          ); // decrease y
        }
      }
      if (rotation >= 90 && rotation < 180) {
        if (characterPosition.y < nextPositon.y) {
          region = walkUpAnimation.getKeyFrame(frame, PlayMode.LOOP);
          characterPosition.y = Math.min(
            characterPosition.y + delta * moveSpeed,
            nextPositon.y
          ); // increase y
        }
        if (characterPosition.x > nextPositon.x) {
          region = walkLeftAnimation.getKeyFrame(frame, PlayMode.LOOP);
          characterPosition.x = Math.max(
            characterPosition.x - delta * moveSpeed,
            nextPositon.x
          ); // decrease x
        }
      }
      if (rotation >= -180 && rotation < -90) {
        if (characterPosition.y > nextPositon.y) {
          region = walkDownAnimation.getKeyFrame(frame, PlayMode.LOOP);
          characterPosition.y = Math.max(
            characterPosition.y - delta * moveSpeed,
            nextPositon.y
          ); // decrease y
        }
        if (characterPosition.x > nextPositon.x) {
          region = walkLeftAnimation.getKeyFrame(frame, PlayMode.LOOP);
          characterPosition.x = Math.max(
            characterPosition.x - delta * moveSpeed,
            nextPositon.x
          ); // decrease x
        }
      }
    } else {
      frame = 0;
    }

    // if (
    //   Math.abs(applePosition.x - characterPosition.x) <= 35 &&
    //   Math.abs(applePosition.y - characterPosition.y) <= 30
    // ) {
    //   applePosition.x = getRandomInt(50, 400);
    //   applePosition.y = getRandomInt(30, 900);
    // }
    batch.setProjection(camera.projectionView.values);
    batch.begin();
    batch.draw(background, 0, 0, 500, 1000);
    // batch.draw(apple, applePosition.x, applePosition.y, 45, 50);
    // console.log(frame);
    region.draw(
      batch,
      characterPosition.x - 50,
      characterPosition.y - 20,
      100,
      100
    );
    batch.end();

    // shapeRenderer.begin();
    // for (let rect of obstacles) {
    //   shapeRenderer.rect(
    //     false,
    //     rect.x,
    //     rect.y,
    //     rect.width,
    //     rect.height,
    //     Color.WHITE
    //   );
    // }
    // if (path.length >= 4) {
    //   for (let i = 2; i < path.length; i += 2) {
    //     shapeRenderer.line(
    //       path[i - 2],
    //       path[i - 1],
    //       path[i],
    //       path[i + 1],
    //       Color.WHITE
    //     );
    //   }
    // }
    // shapeRenderer.end();
  });
};
init();
