import { System, Inject } from "flat-ecs";
import { Color, ShapeRenderer, Vector2, ViewportInputHandler } from "gdxts";
import { Constants } from "../Constants";

export class ShapeRendererSystem extends System {
  @Inject("slingPos") slingPos: Vector2;
  @Inject("shapeRenderer") shapeRenderer: ShapeRenderer;
  @Inject("inputHandle") inputHandle: ViewportInputHandler;

  initialized() {
    inputHandler.addEventListener(InputEvent.TouchMove, () => {
      if (inputHandler.isTouched()) {
        this.slingPos = inputHandler.getTouchedWorldCoord();
      }
    });
  }
  process(): void {
    this.shapeRenderer.begin();
    this.shapeRenderer.circle(
      true,
      this.slingPos.x,
      this.slingPos.y,
      0.1 * Constants.METER_TO_WORLD,
      Color.BLUE
    );
    this.shapeRenderer.end();
  }
}
