export const project = (out, vec3, mat4) => {
  const [x, y, z] = vec3;
  const m = mat4;
  const a00 = m[0],
        a01 = m[1],
        a02 = m[2],
        a03 = m[3],
        a10 = m[4],
        a11 = m[5],
        a12 = m[6],
        a13 = m[7],
        a20 = m[8],
        a21 = m[9],
        a22 = m[10],
        a23 = m[11],
        a30 = m[12],
        a31 = m[13],
        a32 = m[14],
        a33 = m[15];
  const l_w = 1 / (x * a03 + y * a13 + z * a23 + a33);
  out[0] = (x * a00 + y * a10 + z * a20 + a30) * l_w;
  out[1] = (x * a01 + y * a11 + z * a21 + a31) * l_w;
  out[2] = (x * a02 + y * a12 + z * a22 + a32) * l_w;
  return out;
};
export const unproject = (out, vec3, invMat4, viewX, viewY, viewWidth, viewHeight) => {
  let [x, y, z] = vec3;
  x = x - viewX;
  y = viewHeight - y - 1;
  y = y - viewY;
  out[0] = 2 * x / viewWidth - 1;
  out[1] = 2 * y / viewHeight - 1;
  out[2] = 2 * z - 1;
  return project(out, out, invMat4);
};
