import { time } from "console";
import {
  createGameLoop,
  createStage,
  createViewport,
  PolygonBatch,
  ShapeRenderer,
  Texture,
  Vector2,
  BitmapFont,
  TextureRegion,
  Color,
} from "gdxts";

export const init = async () => {
  const stage = createStage();
  const canvas = stage.getCanvas();
  const viewport = createViewport(canvas, 1500, 2000);
  const gl = viewport.getContext();
  const shapeRenderer = new ShapeRenderer(gl);
  const batch = new PolygonBatch(gl);
  const camera = viewport.getCamera();

  const background = await Texture.load(gl, "./TetrisBg.png");
  const border = await Texture.load(gl, "./border.png");
  const bgRight = await Texture.load(gl, "./bg1.png");
  const block = await Texture.load(gl, "./7251.png");
  const redBlock = await Texture.load(gl, "./RedBlock.png");
  const purpleBlock = await Texture.load(gl, "./PurpleBlock.png");
  const orangeBlock = await Texture.load(gl, "./OrangeBlock.png");
  const yellowBlock = await Texture.load(gl, "./YellowBlock.png");
  const blueBlock = await Texture.load(gl, "./BlueBlock.png");
  const blueSkyBlock = await Texture.load(gl, "./BlueSkyBlock.png");
  const greenBlock = await Texture.load(gl, "./GreenBlock.png");

  let map = new Array();

  //tetromino
  const squareBlock = [
    [1, 1],
    [1, 1],
  ];
  //I
  const iBlock1 = [2, 2, 2, 2];
  const iBlock2 = [[2], [2], [2], [2]];

  //J
  const jBlock1 = [
    [3, 0, 0],
    [3, 3, 3],
  ];
  const jBlock2 = [
    [3, 3],
    [3, 0],
    [3, 0],
  ];
  const jBlock3 = [
    [3, 3, 3],
    [0, 0, 3],
  ];
  //L
  const LBlock1 = [
    [0, 0, 4],
    [4, 4, 4],
  ];
  const LBlock2 = [
    [4, 4],
    [0, 4],
    [0, 4],
  ];
  //s
  const sBlock1 = [
    [0, 5, 5],
    [5, 5, 0],
  ];
  const sBlock2 = [
    [5, 0],
    [5, 5],
    [0, 5],
  ];
  //t
  const tBlock1 = [
    [0, 6, 0],
    [6, 6, 6],
  ];
  const tBlock2 = [
    [6, 0],
    [6, 6],
    [6, 0],
  ];
  const tBlock3 = [
    [7, 7, 7],
    [0, 7, 0],
  ];
  const tBlock4 = [
    [0, 7],
    [7, 7],
    [0, 7],
  ];
  //z
  const zBlock1 = [
    [8, 8, 0],
    [0, 8, 8],
  ];
  const zBlock2 = [
    [8, 0],
    [8, 8],
    [0, 8],
  ];

  //config game

  let blockPosition = new Vector2(83, 83);
  let currentBlock = squareBlock; // error multidimensional array
  let timeDelay = 0;
  let rowBlock = 0;
  let colBlock = 0;

  function getRandomInt(min: number, max: number) {
    return Math.floor(Math.random() * (max - min)) + min;
  }
  // draw the map
  for (let row = 0; row < 23; row++) {
    //12x24
    map[row] = new Array();
    for (let col = 0; col < 11; col++) {
      map[row].push(0);
    }
  }

  function blockTracking() {
    for (let row = 0; row < currentBlock.length; row++) {
      for (let col = 0; col < currentBlock[row].length; col++) {
        map[21 + row][5 + col] = currentBlock[row][col];
      }
    }
  }

  function drawTetromino() {
    for (let row = 0; row < map.length; row++) {
      for (let col = 0; col < map[row].length; col++) {
        if (map[row][col] !== 0) {
          colBlock = col;
          rowBlock = row;
          batch.draw(
            blueSkyBlock,
            colBlock * blockPosition.x,
            rowBlock * blockPosition.y,
            83,
            83
          );
        }
      }
    }
  }

  window.addEventListener("keypress", function (e) {
    if (e) {
    }
  });

  blockTracking();
  createGameLoop((delta: number) => {
    timeDelay += delta;
    if (timeDelay >= 0.5) {
      timeDelay = 0;
      for (let row = 0; row < map.length; row++) {
        for (let col = 0; col < map[row].length; col++) {
          if (map[row][col] !== 0) {
            map[row - 1][col] = map[row][col];
            map[row - 1][col + 1] = map[row][col + 1];
            if (row >= 3) {
              map[row][col] = 0;
              map[row][col + 1] = 0;
            }
            console.log(map);
          }
        }
      }
    }

    gl.clear(gl.COLOR_BUFFER_BIT);
    shapeRenderer.setProjection(camera.projectionView.values);
    shapeRenderer.begin();
    shapeRenderer.rect(true, 1, 0, 1000, 2000);
    shapeRenderer.end();

    //draw border
    batch.setProjection(camera.projectionView.values);
    batch.begin();
    // batch.draw(background, 0, 0, 1000, 2000);
    drawTetromino();
    // batch.draw(border, 0, 0, 1000, 2000);
    batch.draw(bgRight, 1000, 0, 500, 2000);
    batch.end();
  });
};

init();
