import { b2Body, b2BodyType } from "box2d.ts";
import { System, Inject } from "flat-ecs";
import {
  AssetManager,
  Color,
  InputEvent,
  PolygonBatch,
  ShapeRenderer,
  Texture,
  Vector2,
  ViewportInputHandler,
} from "gdxts";
import { Constants } from "../Constants";

export class SlingshotRenderSystem extends System {
  slingShot: Texture;

  @Inject("shapeRenderer") shapeRenderer: ShapeRenderer;
  @Inject("inputHandler") inputHandler: ViewportInputHandler;
  @Inject("assetManager") assetManager: AssetManager;
  @Inject("batch") batch: PolygonBatch;
  @Inject("slingPos") slingPos: Vector2;
  @Inject("originPosition") originPosition: Vector2;
  @Inject("bird") bird: b2Body;

  initialized() {
    this.slingShot = this.assetManager.getTexture("slingShot") as Texture;
    this.inputHandler.addEventListener(InputEvent.TouchMove, () => {
      if (this.inputHandler.isTouched()) {
        this.slingPos = this.inputHandler.getTouchedWorldCoord();
      }
    });
    this.inputHandler.addEventListener(InputEvent.TouchEnd, () => {
      this.bird.SetType(b2BodyType.b2_dynamicBody);
      this.bird.ApplyLinearImpulseToCenter(
        {
          x:
            (7 * (this.originPosition.x - this.slingPos.x)) /
            Constants.METER_TO_WORLD,
          y:
            (7 * (this.originPosition.y - this.slingPos.y)) /
            Constants.METER_TO_WORLD,
        },
        true
      );
    });
  }

  process(): void {
    this.shapeRenderer.begin();
    this.shapeRenderer.circle(
      true,
      this.slingPos.x,
      this.slingPos.y,
      0.1 * Constants.METER_TO_WORLD,
      Color.BLUE
    );
    this.shapeRenderer.end();
    this.batch.begin();
    this.batch.draw(
      this.slingShot,
      2 * Constants.METER_TO_WORLD,
      3.5 * Constants.METER_TO_WORLD,
      0.25 * Constants.METER_TO_WORLD,
      0.7 * Constants.METER_TO_WORLD
    );
    this.batch.end();
  }
}
