import { System, Inject } from "flat-ecs";
import { AssetManager, PolygonBatch, Texture } from "gdxts";
import { Constants } from "../Constants";

export class BirdRenderSystem extends System {
  bird: Texture;
  ballX: number;
  ballY: number;

  @Inject("gl") gl: WebGLRenderingContext;
  @Inject("assetManager") assetManager: AssetManager;
  @Inject("batch") batch: PolygonBatch;
  initialized(): void {
    this.bird = this.assetManager.getTexture("bird") as Texture;
  }
  process(): void {
    console.log(Constants.METER_TO_WORLD);
    this.batch.begin();
    this.batch.draw(
      this.bird,
      this.ballX * Constants.METER_TO_WORLD - 25,
      this.ballY * Constants.METER_TO_WORLD - 25,
      Constants.BOX_SIZE * Constants.METER_TO_WORLD,
      Constants.BOX_SIZE * Constants.METER_TO_WORLD
    );
    this.batch.end();
  }
}
