  const block = await Texture.load(gl, "./borderBlock.png");
  const mainBlock = await Texture.load(gl, "./GreenBlock.png");
  const black = new Color(0, 0, 0, 1);

  //config game
  const ROWGAME = 24;
  const COLGAME = 12;
  let map: any[] = [];
  let blockSize = new Vector2(83, 83);
  let delayTime = 0;

  //I
  const iBlock = [[1], [1], [1], [1]];

  //L
  const lBlock = [
    [0, 0, 1],
    [1, 1, 1],
  ];

  //L-reverse
  const lReverseBlock = [
    [1, 0, 0],
    [1, 1, 1],
  ];

  // Square
  const sBlock = [
    [1, 1],
    [1, 1],
  ];

  // Z
  const zBlock = [
    [0, 1, 1],
    [1, 1, 0],
  ];

  // Z-reverse
  const zReverseBlock = [
    [1, 1, 0],
    [0, 1, 1],
  ];

  // T
  const tBlock = [
    [0, 1, 0],
    [1, 1, 1],
  ];

  const blocks = [
    iBlock,
    lReverseBlock,
    lBlock,
    sBlock,
    zBlock,
    zReverseBlock,
    tBlock,
  ];
  console.log(map);
  let currentBlock = blocks[6]; //
  let nextBlock = blocks[getRandomInt(0, 6)];
  let currentPosition: number[] = [];

  function getRandomInt(min: number, max: number) {
    return Math.floor(Math.random() * (max - min)) + min;
  }

  // draw the map
  function mapGame() {
    for (let row = 0; row < ROWGAME; row++) {
      map[row] = [];
      for (let col = 0; col < COLGAME; col++) {
        if (row >= 1) {
          map[row].push(0);
        } else {
          map[row].push(1);
        }
      }
      for (let col = 0; col < map[row].length; col++) {
        map[row][0] = 1;
        map[row][11] = 1;
      }
    }
  }

  function blockOnMap() {
    currentPosition = [];
    for (let row = 0; row < currentBlock.length; row++) {
      for (let col = 0; col < currentBlock[row].length; col++) {
        map[ROWGAME - 1 - row][COLGAME / 2 - 1 + col] = currentBlock[row][col];
        if (map[ROWGAME - 1 - row][COLGAME / 2 - 1 + col]) {
          currentPosition.push(ROWGAME - 1 - row, COLGAME / 2 - 1 + col);
        }
        console.warn(currentPosition);
      }
    }
  }

  function drawTetromino() {
    for (let row = 0; row < map.length; row++) {
      for (let col = 0; col < map[row].length; col++) {
        if (map[row][col] !== 0) {
          batch.draw(
            map[row][col] === 1 ? block : mainBlock,
            col * blockSize.x,
            row * blockSize.y,
            83,
            83
          );
        } // scan to draw
      }
    } //
  }

  function collisionHandmade() {
    switch (currentBlock) {
      case blocks[0]:
        if (map[currentPosition[6] - 1][currentPosition[7]] === 0) {
          map[currentPosition[0] - 1][currentPosition[1]] =
            map[currentPosition[0]][currentPosition[1]];
          map[currentPosition[2] - 1][currentPosition[3]] =
            map[currentPosition[2]][currentPosition[3]];
          map[currentPosition[4] - 1][currentPosition[5]] =
            map[currentPosition[4]][currentPosition[5]];
          //undraw
          map[currentPosition[6] - 1][currentPosition[7]] = 1;
          map[currentPosition[0]][currentPosition[1]] = 0;
          for (let i = 0; i < currentPosition.length; i += 2) {
            currentPosition[i] -= 1;
          }
        } else {
          currentBlock = nextBlock;
          nextBlock = blocks[getRandomInt(0, 6)];
          blockOnMap();
        }

        break;
      case blocks[1]:
        if (
          map[currentPosition[2] - 1][currentPosition[3]] === 0 &&
          map[currentPosition[4] - 1][currentPosition[5]] === 0 &&
          map[currentPosition[6] - 1][currentPosition[7]] === 0
        ) {
          map[currentPosition[0] - 1][currentPosition[1]] =
            map[currentPosition[0]][currentPosition[1]];
          map[currentPosition[2] - 1][currentPosition[3]] = 1;
          map[currentPosition[4] - 1][currentPosition[5]] =
            map[currentPosition[4]][currentPosition[5]];
          map[currentPosition[6] - 1][currentPosition[7]] =
            map[currentPosition[6]][currentPosition[7]];
          //undraw
