          blockOnMap();
        }
        break;
      case blocks[4]:
        if (
          map[currentPosition[4] - 1][currentPosition[5]] === 0 &&
          map[currentPosition[6] - 1][currentPosition[7]] === 0
        ) {
          map[currentPosition[4] - 1][currentPosition[5]] =
            map[currentPosition[4]][currentPosition[5]];
          map[currentPosition[6] - 1][currentPosition[7]] =
            map[currentPosition[6]][currentPosition[7]];
          map[currentPosition[2] - 1][currentPosition[3]] =
            map[currentPosition[2]][currentPosition[3]];

          //undraw
          map[currentPosition[0]][currentPosition[1]] = 0;
          map[currentPosition[2]][currentPosition[3]] = 0;
          map[currentPosition[4]][currentPosition[5]] = 0;

          for (let i = 0; i < currentPosition.length; i += 2) {
            currentPosition[i] -= 1;
          }
        } else {
          currentBlock = nextBlock;
          nextBlock = blocks[getRandomInt(0, 6)];
          blockOnMap();
        }
        break;
      case blocks[5]:
        if (
          map[currentPosition[0] - 1][currentPosition[1]] === 0 &&
          map[currentPosition[4] - 1][currentPosition[5]] === 0 &&
          map[currentPosition[6] - 1][currentPosition[7]] === 0
        ) {
          map[currentPosition[0] - 1][currentPosition[1]] =
            map[currentPosition[0]][currentPosition[1]];
          map[currentPosition[4] - 1][currentPosition[5]] =
            map[currentPosition[4]][currentPosition[5]];
          map[currentPosition[6] - 1][currentPosition[7]] =
