export default class World {
  constructor(worldState) {
    this.activeSystems = [];
    this.passiveSystems = [];
    this.worldState = worldState;
  }

  addSystem(system, active = true) {
    if (active) {
      this.activeSystems.push(system);
    } else {
      this.passiveSystems.push(system);
    }
  }

  processActive(delta) {
    for (let system of this.activeSystems) {
      system.update(delta, this.worldState);
    }
  }

  processPassive(delta) {
    for (let system of this.passiveSystems) {
      system.update(delta, this.worldState);
    }
  }

  dispose() {
    for (let system of this.activeSystems) {
      if (system.dispose) {
        system.dispose();
      }
    }

    for (let system of this.passiveSystems) {
      if (system.dispose) {
        system.dispose();
      }
    }
  }

}
