import {
  createBatch,
  createGameLoop,
  createStage,
  createViewport,
  createWhiteTexture,
  InputHandler,
} from "gdxjs";

export const initTest = () => {
  const stage = createStage();
  stage.show();

  const viewport = createViewport(stage.getCanvas(), 50, 100, {
    pixelRatio: Math.min(2, window.devicePixelRatio),
    contextOption: {
      antialias: true,
    },
  });
  const gl = viewport.getContext();
  const batch = createBatch(gl);
  const camera = viewport.getCamera();
  const whiteText = createWhiteTexture(gl);

  const inputHandler = new InputHandler(stage.getCanvas());
  inputHandler.addEventListener("touchEnd", () => {});

  const human: {
    texture: Texture;
    display: Vector2;
    width: number;
    height: number;
  } = {
    texture: whiteText as Texture,
    display: new Vector2(50 / 2, 100 / 2),
    width: 8,
    height: 20,
  };

  gl.clearColor(0, 0, 0, 1);
  const loop = createGameLoop((delta) => {
    gl.clear(gl.COLOR_BUFFER_BIT);

    batch.setProjection(camera.combined);
    batch.begin();
    batch.draw(whiteText, 0, 0, 50, 100);

    batch.setColor(1, 0, 0, 1);
    batch.draw(
      human.texture,
      human.display.x,
      human.display.y,
      human.width,
      human.height
    );
    batch.setColor(1, 1, 1, 1);
    batch.end();
  });
};
