import { System, Inject } from "flat-ecs";
import { ShapeRenderer } from "gdxts";

export class ShapeRendererSystem extends System {
  @Inject("shapeRenderer") shapeRenderer: ShapeRenderer;
  process(): void {
    shapeRenderer.begin();
    shapeRenderer.circle(
      true,
      slingPos.x,
      slingPos.y,
      0.1 * METER_TO_WORLD,
      Color.BLUE
    );
    shapeRenderer.end();
  }
}
