import {
  createGameLoop,
  createStage,
  createViewport,
  PolygonBatch,
  ShapeRenderer,
  Texture,
  Vector2,
  BitmapFont,
  TextureRegion,
  Color,
} from "gdxts";

const BOARD_ROWS = 22;
const BOARD_COLS = 10;
const GAME_WIDTH = 1500;
const GAME_HEIGHT = 2000;

type Coordinate = {
  x: number;
  y: number;
};

export const init2 = async () => {
  const stage = createStage();
  const canvas = stage.getCanvas();
  const viewport = createViewport(canvas, GAME_WIDTH, GAME_HEIGHT);
  const gl = viewport.getContext();
  const shapeRenderer = new ShapeRenderer(gl);
  const batch = new PolygonBatch(gl);
  const camera = viewport.getCamera();

  const redBlock = await Texture.load(gl, "./RedBlock.png");

  const board: number[] = [];

  //I
  const iBlock = [[1], [1], [1], [1]];

  //L
  const lBlock = [
    [0, 0, 2],
    [2, 2, 2],
  ];

  //L-reverse
  const lReverseBlock = [
    [3, 0, 0],
    [3, 3, 3],
  ];

  // Square
  const sBlock = [
    [5, 5],
    [5, 5],
  ];

  // Z
  const zBlock = [
    [0, 6, 6],
    [6, 6, 0],
  ];

  // Z-reverse
  const zReverseBlock = [
    [7, 7, 0],
    [0, 7, 7],
  ];

  // T
  const tBlock = [
    [0, 8, 0],
    [8, 8, 8],
  ];

  const blocks = [
    iBlock,
    lReverseBlock,
    lBlock,
    sBlock,
    zBlock,
    zReverseBlock,
    tBlock,
  ];

  // generate board
  for (let i = 0; i < BOARD_ROWS; i++) {
    for (let j = 0; j < BOARD_COLS; j++) {
      board.push(0);
    }
  }

  let timeDelay = 0;
  const black = new Color(0, 0, 0, 1);

  const mapNumberToColor = (number: number): Color => {
    if (number % 2 === 0) {
      return Color.BLUE;
    } else {
      return Color.GREEN;
    }

    // switch (number) {
    // }
  };

  const indexToCoord = (index: number): Coordinate => {};

  const drawBoard = () => {
    for (let i = 0; i < board.length; i++) {
      const color = mapNumberToColor(board[i]);

      shapeRenderer.rect(true);
    }
  };

  createGameLoop((delta: number) => {
    timeDelay += delta;
    if (timeDelay >= 0.5) {
    }

    gl.clear(gl.COLOR_BUFFER_BIT);
    shapeRenderer.setProjection(camera.projectionView.values);
    shapeRenderer.begin();
    shapeRenderer.rect(true, 0, 0, GAME_WIDTH, GAME_HEIGHT, black);
    drawBoard();
    shapeRenderer.end();

    //draw border
    // batch.setProjection(camera.projectionView.values);
    // batch.begin();
    // batch.end();
  });
};
