import { System, Inject } from "flat-ecs";
import {
  AssetManager,
  PolygonBatch,
  Texture,
  ViewportInputHandler,
  ShapeRenderer,
  Vector2,
  Color,
  OrthoCamera,
} from "gdxts";
import { Constants } from "../Constants";

export class SlingshotRenderSystem extends System {
  slingShot: Texture;
  slingPos: Vector2;
  shapeRenderer: ShapeRenderer;
  @Inject("inputHandle") inputHandle: ViewportInputHandler;
  @Inject("assetManager") assetManager: AssetManager;
  @Inject("batch") batch: PolygonBatch;
  @Inject("camera") camera: OrthoCamera;

  process(): void {
    console.log("dcm");
    this.shapeRenderer.setProjection(this.camera.combined);
    this.shapeRenderer.begin();
    this.shapeRenderer.circle(
      true,
      this.slingPos.x,
      this.slingPos.y,
      0.1 * Constants.METER_TO_WORLD,
      Color.BLUE
    );
    this.shapeRenderer.end();

    this.batch.begin();
    this.batch.draw(
      this.slingShot,
      2 * Constants.METER_TO_WORLD,
      3.5 * Constants.METER_TO_WORLD,
      0.25 * Constants.METER_TO_WORLD,
      0.7 * Constants.METER_TO_WORLD
    );

    this.batch.end();
  }
}
