import {
  createGameLoop,
  createStage,
  createViewport,
  PolygonBatch,
  ShapeRenderer,
  Texture,
  Vector2,
  BitmapFont,
  TextureRegion,
  Color,
} from "gdxts";

import { init2 } from "./index2";

export const init = async () => {
  const stage = createStage();
  const canvas = stage.getCanvas();
  const viewport = createViewport(canvas, 1500, 2000);
  const gl = viewport.getContext();
  const shapeRenderer = new ShapeRenderer(gl);
  const batch = new PolygonBatch(gl);
  const camera = viewport.getCamera();

  const background = await Texture.load(gl, "./TetrisBg.png");
  const border = await Texture.load(gl, "./border.png");
  const bgRight = await Texture.load(gl, "./bg1.png");
  const block = await Texture.load(gl, "./borderBlock.png");
  const black = new Color(0, 0, 0, 1);

  let map = new Array();

  //I
  const iBlock = [[1], [1], [2], [1]];

  //L
  const lBlock = [
    [0, 0, 1],
    [1, 1, 1],
  ];

  //L-reverse
  const lReverseBlock = [
    [1, 0, 0],
    [1, 1, 1],
  ];

  // Square
  const sBlock = [
    [1, 1],
    [1, 1],
  ];

  // Z
  const zBlock = [
    [0, 1, 1],
    [1, 1, 0],
  ];

  // Z-reverse
  const zReverseBlock = [
    [1, 1, 0],
    [0, 1, 1],
  ];

  // T
  const tBlock = [
    [0, 1, 0],
    [1, 1, 1],
  ];

  const blocks = [
    iBlock,
    lReverseBlock,
    lBlock,
    sBlock,
    zBlock,
    zReverseBlock,
    tBlock,
  ];

  //config game

  let blockPosition = new Vector2(83, 83);
  let currentBlock = blocks[0]; //
  let nextBlock = blocks[getRandomInt(0, 6)];
  let timeDelay = 0;

  function getRandomInt(min: number, max: number) {
    return Math.floor(Math.random() * (max - min)) + min;
  }
  // draw the map
  for (let row = 0; row < 24; row++) {
    //12x24
    map[row] = new Array();
    for (let col = 0; col < 12; col++) {
      if (row >= 1) {
        map[row].push(0);
      } else {
        map[row].push(1);
      }
    }
    for (let col = 0; col < map[row].length; col++) {
      map[row][0] = 1;
      map[row][11] = 1;
    }
  }

  function blockTracking() {
    for (let row = 0; row < currentBlock.length; row++) {
      for (let col = 0; col < currentBlock[row].length; col++) {
        map[23 - row][5 + col] = currentBlock[row][col];
      }
    }
  }

  function drawTetromino() {
    for (let row = 0; row < map.length; row++) {
      for (let col = 0; col < map[row].length; col++) {
        if (map[row][col] !== 0 && map[row][col] !== 9) {
          batch.draw(
            block,
            col * blockPosition.x,
            row * blockPosition.y,
            83,
            83
          );
        } // scan to draw
      }
    }
  }

  window.addEventListener("keypress", function (e) {
    if (e) {
    }
  });

  blockTracking();
  createGameLoop((delta: number) => {
    timeDelay += delta;
    if (timeDelay >= 0.5) {
      timeDelay = 0;

      for (let row = 1; row < map.length; row++) {
        for (let col = 1; col < map[row].length - 1; col++) {
          if (map[row - 1][col] === 0 && map[row][col] !== 0) {
            [map[row][col], map[row - 1][col]] = [
              map[row - 1][col],
              map[row][col],
            ];
          }
        }
      }
      for (let row = 23; row > 1; row--) {
        for (let col = 1; col < map[row].length - 1; col++) {
          if (map[row][col] !== 0) {
            console.log(row + " work");
          }
        }
      }

      console.log(map);
    }
    gl.clear(gl.COLOR_BUFFER_BIT);
    shapeRenderer.setProjection(camera.projectionView.values);
    shapeRenderer.begin();
    shapeRenderer.rect(true, 0, 0, 1000, 2000, black);
    shapeRenderer.end();

    //draw border
    batch.setProjection(camera.projectionView.values);
    batch.begin();

    // batch.draw(background, 0, 0, 1000, 2000);
    drawTetromino();
    // batch.draw(border, 0, 0, 1000, 2000);
    batch.draw(bgRight, 1000, 0, 500, 2000);
    batch.end();
  });
};

init();
// init2();
