import {
  createGameLoop,
  createStage,
  createViewport,
  PolygonBatch,
  ShapeRenderer,
  Texture,
  Vector2,
  BitmapFont,
  TextureRegion,
  Color,
} from "gdxts";

import { init2 } from "./index2";

export const init = async () => {
  const stage = createStage();
  const canvas = stage.getCanvas();
  const viewport = createViewport(canvas, 1500, 2000);
  const gl = viewport.getContext();
  const shapeRenderer = new ShapeRenderer(gl);
  const batch = new PolygonBatch(gl);
  const camera = viewport.getCamera();

  // const background = await Texture.load(gl, "./TetrisBg.png");
  // const border = await Texture.load(gl, "./border.png");
  const bgRight = await Texture.load(gl, "./bg1.png");
  const block = await Texture.load(gl, "./borderBlock.png");
  const mainBlock = await Texture.load(gl, "./GreenBlock.png");
  const black = new Color(0, 0, 0, 1);

  //config game
  const ROWGAME = 24;
  const COLGAME = 12;
  let map: any[] = [];
  let blockSize = new Vector2(83, 83);
  let delayTime = 0;

  //I
  const iBlock = [[1], [1], [1], [2]];

  //L
  const lBlock = [
    [0, 0, 1],
    [2, 2, 2],
  ];

  //L-reverse
  const lReverseBlock = [
    [1, 0, 0],
    [2, 2, 2],
  ];

  // Square
  const sBlock = [
    [1, 1],
    [2, 2],
  ];

  // Z
  const zBlock = [
    [0, 1, 2],
    [2, 2, 0],
  ];

  // Z-reverse
  const zReverseBlock = [
    [2, 1, 0],
    [0, 2, 2],
  ];

  // T
  const tBlock = [
    [0, 1, 0],
    [2, 2, 2],
  ];

  const blocks = [
    iBlock,
    lReverseBlock,
    lBlock,
    sBlock,
    zBlock,
    zReverseBlock,
    tBlock,
  ];

  let currentBlock = blocks[1]; //
  let nextBlock = blocks[getRandomInt(0, 6)];
  let currentPosition: number[] = [];

  function getRandomInt(min: number, max: number) {
    return Math.floor(Math.random() * (max - min)) + min;
  }
  // draw the map
  for (let row = 0; row < ROWGAME; row++) {
    map[row] = [];
    for (let col = 0; col < COLGAME; col++) {
      if (row >= 1) {
        map[row].push(0);
      } else {
        map[row].push(1);
      }
    }
    for (let col = 0; col < map[row].length; col++) {
      map[row][0] = 1;
      map[row][11] = 1;
    }
  }

  function blockOnMap() {
    currentPosition = [];
    for (let row = 0; row < currentBlock.length; row++) {
      for (let col = 0; col < currentBlock[row].length; col++) {
        map[ROWGAME - 1 - row][COLGAME / 2 - 1 + col] = currentBlock[row][col];
        if (map[ROWGAME - 1 - row][COLGAME / 2 - 1 + col]) {
          currentPosition.push(ROWGAME - 1 - row, COLGAME / 2 - 1 + col);
        }
        console.warn(currentPosition);
      }
    }
  }

  function drawTetromino() {
    for (let row = 0; row < map.length; row++) {
      for (let col = 0; col < map[row].length; col++) {
        if (map[row][col] !== 0) {
          batch.draw(
            map[row][col] === 1 ? block : mainBlock,
            col * blockSize.x,
            row * blockSize.y,
            83,
            83
          );
        } // scan to draw
      }
    } //
  }

  window.addEventListener("keypress", function (e) {
    if (e) {
    }
  });

  blockOnMap();
  createGameLoop((delta: number) => {
    delayTime += delta;
    if (delayTime >= 0.5) {
      delayTime = 0;
      switch (currentBlock) {
        case blocks[0]:
          for (let row = 0; row < map.length; row++) {
            for (let col = 0; col < map[row].length; col++) {
              if (map[row][col] === 2) {
                if (!map[row - 1][col]) {
                  map[row - 1][col] = 2;
                  map[row][col] = 1;
                  map[row + 1][col] = 1;
                  map[row + 3][col] = 0;
                } else {
                  currentBlock = nextBlock;
                  nextBlock = blocks[getRandomInt(0, 6)];
                  blockOnMap();
                }
              }
            }
          }
          break;

        default:
          break;
      }
    }
    gl.clear(gl.COLOR_BUFFER_BIT);
    shapeRenderer.setProjection(camera.projectionView.values);
    shapeRenderer.begin();
    shapeRenderer.rect(true, 0, 0, 1000, 2000, black);
    shapeRenderer.end();
    console.log(currentBlock);

    //draw border
    batch.setProjection(camera.projectionView.values);
    batch.begin();
    drawTetromino();
    batch.draw(bgRight, 1000, 0, 500, 2000);
    batch.end();
  });
};

init();
// init2();
